import requests

# NodeMCU IP adresi ve endpoint
url = "http://192.168.1.37/upload/"  # NodeMCU'nun IP adresini yazın
headers = {'Content-Type': 'text/plain'}

# txt dosyasını okuma ve veriyi göndermek
# with open("emote_base64.txt", "r") as file:
#     data = file.read()

data = ""


response = requests.post(url, headers=headers, data=data)

if response.status_code == 200:
    print("Veri başarıyla gönderildi.")
else:
    print(f"Veri gönderilirken bir hata oluştu: {response.status_code}")
